import React, { Fragment, useState, useEffect } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Redirect
} from "react-router-dom";



//components

import Login from "./pages/Login";
import Register from "./pages/Register";
import Dashboard from "./pages/Dashboard";
import Home from "./pages/Home";
import RestaurantDetailPage from "./pages/RestaurantDetailPage";
import UpdatePage from "./pages/UpdatePage";

toast.configure();

function App() {

  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const setAuth = (boolean) => {
    setIsAuthenticated(boolean);
  }

  async function isAuth() {
    try{

      const response = await fetch("http://localhost:5000/auth/is-verify", {
        method: "GET",
        headers: {token:localStorage.token}
      })

      const parseRes = await response.json();

      parseRes === true ? setIsAuthenticated(true) : setIsAuthenticated(false)

    } catch (err) {
      console.error(err.message)
    }
  }

  useEffect(() => {
    isAuth()
  })

  return (
    <Fragment>
      <Router>
        <div className="container">
          <Switch>
            <Route exact path="/" render={props => isAuthenticated ? <Home {...props} setAuth={setAuth} /> : <Redirect to="/login" /> } />
            <Route exact path="/login" render={props => !isAuthenticated ? <Login {...props} setAuth={setAuth} /> : <Redirect to="/dashboard" /> } />
            <Route exact path="/register" render={props => !isAuthenticated ? <Register {...props} setAuth={setAuth} /> : <Redirect to="/dashboard" /> } />
            <Route exact path="/dashboard" render={props => isAuthenticated ? <Dashboard {...props} setAuth={setAuth} /> : <Redirect to="/login" /> } />
            <Route exact path="/restaurants/:id" render={props => isAuthenticated ? <RestaurantDetailPage {...props} setAuth={setAuth} /> : <Redirect to="/login" /> } />
            <Route exact path="/restaurants/:id/update" render={props => isAuthenticated ? <UpdatePage {...props} setAuth={setAuth} /> : <Redirect to="/login" /> } />
          </Switch>
        </div>
      </Router>
    </Fragment>
  )
}

export default App;
