import React from "react";

const StarRating = ({ rating }) => {
  //rating =4
  const stars = [];
  for (let i = 1; i <= 5; i++) {
    if (i <= rating) {
      stars.push(<i class="fa-solid fa-user"></i>);
    } else if (i === Math.ceil(rating) && !Number.isInteger(rating)) {
      stars.push(<i class="fa-solid fa-user"></i>);
    } else {
      stars.push(<i class="fa-solid fa-user"></i>);
    }
  }
  return <>{stars}</>;
};

export default StarRating;
