import React, { useContext, useEffect } from "react";
import { useParams } from "react-router-dom";
import { RestaurantsContext } from "../context/RestaurantsContext";
import RestaurantFinder from "../apis/RestaurantFinder";
import StarRating from "../components/StarRating";
import Reviews from "../components/Reviews";
import AddReview from "../components/AddReview";
import RestaurantDetailHeader from "../components/RestaurantDetailHeader";
import Cart from "../components/elements/Cart";
import ProductCard from "../components/elements/ProductCard";
import Loading from "../components/Loading";

const RestaurantDetailPage = () => {
  const { id } = useParams();
  const { selectedRestaurant, setSelectedRestaurant } = useContext(
    RestaurantsContext
  );

  // console.log(selectedRestaurant);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await RestaurantFinder.get(`/${id}`);
        // console.log(response);

        setSelectedRestaurant(response.data.data);
      } catch (err) {
        console.log(err);
      }
    };

    fetchData();
  }, []);


  if(selectedRestaurant === null) {
    return (
      <Loading />
    )
  }


  return (
    // <div>
    //   {selectedRestaurant && (
    //     <>
    //       <h1 className="text-center display-1">
    //         {selectedRestaurant.restaurant.name}
    //       </h1>
    //       <div className="text-center">
    //         <StarRating rating={selectedRestaurant.restaurant.average_rating} />
    //         <span className="text-warning ml-1">
    //           {selectedRestaurant.restaurant.count
    //             ? `(${selectedRestaurant.restaurant.count})`
    //             : "(0)"}
    //         </span>
    //       </div>
    //       <div className="mt-3">
    //         {/* {console.log(selectedRestaurant.reviews[selectedRestaurant.reviews.length-1, selectedRestaurant.reviews.length-2])} */}
    //         <Reviews reviews={selectedRestaurant.reviews} />
    //       </div>
    //       <AddReview />
    //     </>
    //   )}
    // </div>

    <div className='md:container mx-auto pt-8 relative'>
      <RestaurantDetailHeader
      name={selectedRestaurant.restaurant.name}
      rating={selectedRestaurant.restaurant.rating}
      review_count = {selectedRestaurant.restaurant.count}
      review1 = {selectedRestaurant.reviews[selectedRestaurant.reviews.length-1]}
      review2 = {selectedRestaurant.reviews[selectedRestaurant.reviews.length-2]}
      />
      {console.log(selectedRestaurant.reviews[selectedRestaurant.reviews.length-1])}
      <div className='flex mt-12 gap-x-10'>
        <div className='sticky top-0'>
          <Cart />
        </div>
        <div >
          <div className='my-5'>
            <h1 className='text-2xl font-semibold text-orange-500 mb-2'>Tavuk Dürümler</h1>
            <div className='bg-gray-700 h-px'></div>
          </div>
          <div className='grid grid-cols-2 grid-flow-row gap-5 mt-5'>
            <ProductCard />
            <ProductCard />
            <ProductCard />
            <ProductCard />
            <ProductCard />
            <ProductCard />
            <ProductCard />
            <ProductCard />
            <ProductCard />
          </div>

          <div className='my-5'>
            <h1 className='text-2xl font-semibold text-orange-500 mb-2'>Pizzalar</h1>
            <div className='bg-gray-700 h-px'></div>
          </div>

          <div className='grid grid-cols-2 grid-flow-row gap-5'>
            <ProductCard />
            <ProductCard />
            <ProductCard />
            <ProductCard />
            <ProductCard />
            <ProductCard />
            <ProductCard />
            <ProductCard />
            <ProductCard />
          </div>
        </div>
      </div>
    </div>

  );
};

export default RestaurantDetailPage;
